package com.leautolink.leautocamera.utils;/** * Created by shimeng on 14/6/28. */import android.os.Parcel;import android.os.Parcelable;import com.leautolink.leautocamera.R;/** * 设置分享的内容 * * @author Administrator */public class ShareContentText implements Parcelable {    private String location;    private String content;    private String title;    private String URL;    private String ImagResource;    private int PicResource = R.drawable.img_default;    private int shareWay = SINA_SHARE_WAY_TEXT;    /**     * 文字     */    public static final int SINA_SHARE_WAY_TEXT = 1;    public ShareContentText() {    }    /**     * 构造分享文字类     *     * @param content 分享的文字内容     */    public ShareContentText(String title, String content, String URL, String ImagResource, String location, int PicResource, int shareWay) {        this.title = title;        this.content = content;        this.URL = URL;        this.ImagResource = ImagResource;        this.location = location;        this.PicResource = PicResource;        this.shareWay = shareWay;    }    public void setTitle(String title) {        this.title = title;    }    public void setLocation(String location) {        this.location = location;    }    public String getLocation() {        return this.location;    }    public String getTitle() {        return this.title;    }    public String getContent() {        return this.content;    }    public void setContent(String content) {        this.content = content;    }    public String getURL() {        return this.URL;    }    public void setURL(String URL) {        this.URL = URL;    }    public String getImagResource() {        return this.ImagResource;    }    public void setImagResource(String ImagResource) {        this.ImagResource = ImagResource;    }    public int getPicResource() {        return this.PicResource;    }    public void setPicResource(int PicResource) {        this.PicResource = PicResource;    }    public int getShareWay() {        return this.shareWay;    }    public void setShareWay(int shareWay) {        this.shareWay = shareWay;    }    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel parcel, int flag) {        parcel.writeString(title);        parcel.writeString(content);        parcel.writeString(URL);        parcel.writeString(ImagResource);        parcel.writeString(location);        parcel.writeInt(PicResource);        parcel.writeInt(shareWay);    }    public static final Creator<ShareContentText> CREATOR = new Creator<ShareContentText>() {        public ShareContentText createFromParcel(Parcel in) {            return new ShareContentText(in);        }        public ShareContentText[] newArray(int size) {            return new ShareContentText[size];        }    };    private ShareContentText(Parcel in) {        title = in.readString();        content = in.readString();        URL = in.readString();        ImagResource = in.readString();        location = in.readString();        PicResource = in.readInt();        shareWay = in.readInt();    }}